/**
 * Load Plugins
 */
const { gulp, dest, src, series, watch, task } = require('gulp');
const uglify			= require('gulp-uglify');
const rename			= require('gulp-rename');
const sass				= require('gulp-sass');
const cssClean			= require('gulp-clean-css');
const gulpCopy 			= require('gulp-copy');
const del				= require('del');
const gulpif 			= require('gulp-if');
require('custom-env').env();

let	devMode				= process.env.DEV_MODE;

/**
 * Assets
 */
let assetsSrc			= 'assets/src/';
let assetsDist			= 'assets/dist/';
let nodeSrc				= 'node_modules/';

/**
 * Assets Sources
 */
let jsSrc				= assetsSrc +'js/';
let scssSrc				= assetsSrc +'scss/';

/**
 * Assets Dist
 */
let cssDist				= assetsDist +'css/';
let jsDist				= assetsDist +'js/';
let vendorsDist			= assetsDist +'vendor/';

/**
 * Node Modules
 */
let	bootstrapSrc		= nodeSrc +'bootstrap/**/*';
let	iconPackSrc			= nodeSrc +'@themingisprose/icon-pack/**/*';
let	countDownTimerSrc	= nodeSrc +'countdowntimer/dist/**/*';
let	jquerySrc			= nodeSrc +'jquery/dist/**/*';
let vendorsSrc			= [ bootstrapSrc, iconPackSrc, countDownTimerSrc, jquerySrc ]

/**
 * Copy required dependencies from node_modules/ to assets/dist/vendors/
 *
 * @since Twenty'em 1.4
 */
function vendors(){
	return src(vendorsSrc)
		.pipe(gulpCopy(vendorsDist, { prefix: 1 }));
}

/**
 * Delete assets/dist/css/ and assets/dist/js/ directories
 *
 * @since Twenty'em 1.4
 */
function clean(){
	return del([cssDist, jsDist, vendorsDist]);
}

/**
 * Compile javascript files from assets/src/js/ to assets/dist/js/
 *
 * @since Twenty'em 1.4
 */
function js(){
	return src(jsSrc + '*.js')
		.pipe(gulpif(devMode != 'true', uglify()))
		.pipe(gulpif(devMode != 'true', rename({ extname: '.min.js' })))
		.pipe(dest(jsDist));
}

/**
 * Compile scss files from assets/src/scss/ to assets/dist/css/
 *
 * @since Twenty'em 1.4
 */
function scss(){
	return src(scssSrc + '*.scss')
		.pipe(sass())
		.pipe(gulpif(devMode != 'true', cssClean()))
		.pipe(gulpif(devMode != 'true', rename({ extname: '.min.css' })))
		.pipe(dest(cssDist));
}

exports.default = series( clean, vendors, js, scss );

/**
 * Child Theme process
 * You must define this environment variables in your .env file
 * CHILD_THEME_EXISTS = true
 * CHILD_THEME = ../child-theme-dir (without trailing slash)
 * See env-sample file for details.
 */

let	childExists 		= process.env.CHILD_THEME_EXISTS;
let	childPath			= process.env.CHILD_THEME +'/';
let	childSrc			= childPath +'assets/src/';
let	childScssSrc		= childSrc +'scss/';
let	childJsSrc			= childSrc +'js/';
let	childDist			= childPath +'assets/dist/';
let	childCssDist		= childDist +'css/';
let	childJsDist			= childDist +'js/';

/**
 * Delete Child Theme ../child-theme-dir/assets/dist/css/ and ../child-theme-dir/assets/dist/js/ directories
 *
 * @since Twenty'em 1.4
 */
function childClean(){
	return gulpif(childExists == 'true', del([childCssDist, childJsDist], {force: true}));
}

/**
 * Compile javascript files in Child Theme from ../child-theme-dir/assets/src/js/ to ../child-theme-dir/assets/dist/js/
 *
 * @since Twenty'em 1.4
 */
function childJs(){
	return src(childJsSrc + '*.js')
		.pipe(gulpif(devMode != 'true', uglify()))
		.pipe(gulpif(devMode != 'true', rename({ extname: '.min.js' })))
		.pipe(dest(childJsDist));
}

/**
 * Compile scss files in Child Theme from ../child-theme-dir/assets/src/scss/ to ../child-theme-dir/assets/dist/css/
 *
 * @since Twenty'em 1.4
 */
function childScss(){
	return src(childScssSrc +'*.scss')
		.pipe(sass())
		.pipe(gulpif(devMode != 'true', cssClean()))
		.pipe(gulpif(devMode != 'true', rename({ extname: '.min.css' })))
		.pipe(dest(childCssDist));
}

exports.child = series( childClean, childJs, childScss );

/**
 * Watch files for changes
 * Run 'gulp watch' to deploy file changes
 *
 * @since Twenty'em 1.4
 */
task('watch', () => {
	watch(jsSrc + '*.js', series( js ));
	watch(scssSrc +'*.scss', series( scss ));
})

/**
 * Watch files for changes
 * Run 'gulp watch:child' to deploy file changes in a Child Theme
 *
 * @since Twenty'em 1.4
 */
task('watch:child', () => {
	watch(childJsSrc + '*.js', series( childJs ));
	watch(childScssSrc +'*.scss', series( childScss ));
})
