/**
 * Template Name: Site Map
 *
 * A custom page template to display a site map.
 */

get_header(); ?>

		<section id="main-content" <?php t_em_breakpoint( 'main-content' ); ?>>
			<section id="content" role="main" <?php t_em_breakpoint( 'content' ); ?>>
			<?php do_action( 't_em_action_content_before' ); ?>

				<div id="sitemap-<?php echo get_the_ID(); ?>" class="custom-template custom-template-sitemap">


					<?php
					/** Displaying pages list
					 * @link http://codex.wordpress.org/Function_Reference/wp_list_pages
					 */
					?>
					<h2 id="sitemap-pages"><?php _e( 'Pages', 't_em' ); ?></h2>
					<ul class="list-unstyled"><?php wp_list_pages( array( 'title_li' => '', 'sort_column' => 'menu_order' ) ) ?></ul>

					<?php
					/** Displaying a list of categories
					 * @link http://codex.wordpress.org/Template_Tags/wp_list_categories
					 */
					?>
					<h2 id="sitemap-categories"><?php _e( 'Categories', 't_em' ); ?></h2>
					<ul class="list-unstyled"><?php wp_list_categories( array( 'title_li' => '', 'show_count' => '1' ) ); ?></ul>

					<?php
					/** Displaying posts per category
					 * @link http://codex.wordpress.org/Function_Reference/get_categories
					 */
					?>
					<h2 id="sitemap-posts-per-categories"><?php _e( 'Posts per categories', 't_em' ); ?></h2>
					<?php
					$cats = get_categories();
					foreach ( $cats as $cat ) :
					?>
					<h3><?php echo $cat->cat_name ?></h3>
						<ul class="list-unstyled">
					<?php
						$posts_per_cat = get_posts( array( 'category' => $cat->cat_ID ) );
						foreach ( $posts_per_cat as $post ) : setup_postdata( $post );
					 ?>
							<li>
								<a href="<?php the_permalink(); ?>" title="<?php printf( esc_attr__( 'Permalink to %s', 't_em' ), the_title_attribute( 'echo=0' ) ); ?>" rel="bookmark"><?php the_title(); ?></a>
								<small> - <?php comments_number( '0 Comments', '1 Comment', '% Comments' ); ?></small>
							</li>
					<?php endforeach; wp_reset_postdata(); ?>
						</ul>
					<?php endforeach; ?>
				</div><!-- #sitemap-## -->
				<?php do_action( 't_em_action_content_after' ); ?>
			</section><!-- #content -->
			<?php get_sidebar(); ?>
			<?php get_sidebar( 'alt' ); ?>
		</section><!-- #main-content -->

<?php get_footer(); ?>

