if ( ! function_exists( 't_em_front_page_widgets' ) ) :
/**
 * Pluggable Function: Render Featured Text Widgets in front page if it's is set by the user in
 * "Front Page Options" in admin panel.
 *
 * This function is directly call from custom-front-page.php template
 *
 * @since Twenty'em 1.0
 */
function t_em_front_page_widgets(){
	if ( 'widgets-front-page' == t_em( 'front_page_set' ) ) :
		if ( t_em( 'text_widget_template' ) == 'template-jumbotron' ) :
			$wrap = 'row';
		elseif ( t_em( 'text_widget_template' ) == 'template-features' ) :
			$wrap = 'featurette-group';
		elseif ( t_em( 'text_widget_template' ) == 'template-cards' ) :
			$wrap = 'card-group';
		endif;
	?>
		<section id="featured-widget-area" class="<?php echo t_em( 'text_widget_template' ) ?>">
			<div class="<?php echo $wrap ?>">
				<?php do_action( 't_em_action_custom_front_page_inside_before' ); ?>
				<?php get_template_part( '/template-parts/front', t_em( 'text_widget_template' ) ) ?>
				<?php do_action( 't_em_action_custom_front_page_inside_after' ); ?>
			</div>
		</section><!-- #featured-widget-area -->
<?php
	endif;
}
endif; // function t_em_front_page_widgets()
?>
